// Cascade ACP40-A-GSG-100 "Open" definition
// C0 = -9.30 fF
open=uW_cal_define_standard("O",-9.3e-15);

// You can acces to data as follow :
typeof(open)
open.C0

// Calculate the S11 from those data :
f=2*10^9;  // f=2 GHz
S11=uW_cal_standard(f,open);

// ========================================
// Example with with 12ps delay and no loss
// C0 = 5e-15
// C1= 60 e-27
// C2 = -11 e-36
// C3 = 0.41 e -45

open=uW_cal_define_standard("O",5e-15,60e-27,-11e-36,0.41e-45,'delay=12e-12','loss=0');

// Now you can check the values as follow :
open.C2

// Read the delay
open.delay

// ========================================
// Example with with 5mm offset
// C0 = 5e-15
// C1= 60 e-27
// C2 = -11 e-36
// C3 = 0.41 e -45
// Offset = 5 mm

// Calculate the delay
my_delay=uW_cal_offset2delay(5);

// Standard definition
open=uW_cal_define_standard("O",5e-15,60e-27,-11e-36,0.41e-45,'delay='+string(my_delay));

// Calkit offset
delay=uW_cal_offset2delay(5);

// Open coefficients
open_anritsu=uW_cal_define_standard("O",5e-15,60e-27,-11e-36,0.41e-45,'delay='+string(delay)); 

// Frequency
frequency=(10^9)*linspace(1,20,100).';

// Reflexion coefficient
S=uW_cal_standard(frequency,open_anritsu);

// Display
uW_S2P_display(S)

// Split the Test-fixture in two equal parts
// 1. Get the Thru line
filename=tk_getfile("*.s2p");
S_thru=uW_S2P_read(filename);


// 2. Get the Opens
filename=tk_getfile("*.s2p");
S_opens=uW_S2P_read(filename);


// 3. Split the test-fixture
[Sin,Sout,Gamma_open]=uW_S2P_split(S_thru,S_opens);

// Display the results
xset("window",100);
xname("Open @ DUT ref. plane");
uW_S2P_display(Gamma_open);
xset("window",101);
xname("Input,Output and Total Thru S parameters");
uW_S2P_display(Sin,Sout,S_thru);


// 4. LOAD-PULL De-embedding 
// The function remove the effect of Sin (S2P with port 1 is the input of the test-fixture and port 2 is the input of the DUT)
// and Sout (S2P : port 1 is the output of the DUT and port 2 is the output of the test-fixture).
// Then, it generate a new IV-CAD file.

filename1=tk_getfile("*.cst"); // File to read (before de-embedding)
filename2=tk_getfile("*.cst"); // File to write (after de-embedding)
uW_AMCAD_CST_deembedding(filename1,filename2,Sin,Sout);

// A - Split the Test-fixture in two equal parts
